import ContactForm from "./ContactForm";
import ContactList from './ContactList';

const options = ['contacts', 'name'];
class Phonebook extends Component {
    state = {
        contacts: [],
        name: ''
    }

    handleClick = (item) => {
        this.setState(prevState => ({
            [item]: prevState[item]
        }))
    }
    render() {
        const { contscts, name } = this.state;
        return (
            <div>
                <ContactForm options={options} onAddContact={this.handleClick} />
                <ContactList title={'Contacts'} />
            </div>
        )
    }
}


